mapper.select:
SELECT picture, car_name, model_name, price, zero_to_hundred, max_mileage, car_type, km_kwh, power_kw,  IF(price < 6000, amount, 0) AS amount
FROM charger_view, Subsidy
WHERE brand_id = 1 AND price BETWEEN 1000 AND 15000 AND Subsidy.car_size = charger_view.car_type AND Subsidy.sigoongoo_id = 1;
